function ifScript() {
    var grSUG = new GlideRecord('sys_user_grmember');
    grSUG.addEncodedQuery("user.active=true^group.name=" + gs.getProperty('x_7756_update_set.operations_team_group_name') + "^group.active=true");
    grSUG.setLimit(1);
    grSUG.query();

    if (grSUG.next()) {
        return 'yes';
    }
    new service_guru_us_helper_scoped().info(gs.getProperty('x_7756_update_set.operations_team_group_name') + ' is defined as the Operations team. It is either inactive or does not have any active Members.', true);

    return 'no';
}

