function initialiseParams() {
    workflow.scratchpad.operations_team = '';
	
    var grSUG = new GlideRecord('sys_user_group');
    grSUG.addEncodedQuery("name=" + gs.getProperty('x_7756_update_set.operations_team_group_name') + "^active=true");
    grSUG.setLimit(1);
    grSUG.query();

    if (grSUG.next()) {
        workflow.scratchpad.operations_team = grSUG.sys_id;
        current.assignment_group = grSUG.sys_id;

    }
}

