answer = ifScript();

function ifScript() {
    var mrvs_update_set = current.variables.service_guru_update_set;
    var rowCount = mrvs_update_set.getRowCount();

    var updateNotSetFound = true;

    for (var i = 0; i < rowCount; i++) {
        if (mrvs_update_set.getRow(i).us_name != undefined && mrvs_update_set.getRow(i).us_name != null && mrvs_update_set.getRow(i).us_name != '') {
            updateNotSetFound = false;
        }
    }
    if (updateNotSetFound) {
        return 'yes';
    }
    var val = new x_7756_update_set.service_guru_us_helper_client().allEnvValue454443(current);

    var grScTask = new GlideRecord('sc_task');
    grScTask.addEncodedQuery("request_item=" + current.sys_id);
    grScTask.setLimit(1);
    grScTask.query();

    if (grScTask.next()) {
        grScTask.description = val;
        grScTask.work_notes = val;
        grScTask.update();
    }

    if (val == '') return 'yes';
    workflow.scratchpad.update_source_message = val;
    return 'no';
}

