answer = ifScript();

function ifScript() {
    var obj_client = new x_7756_update_set.service_guru_us_helper_client();
    var mrvs_update_set = current.variables.service_guru_update_set;
    var rowCount = mrvs_update_set.getRowCount();

    var invalid_components = '',
        temp = '';

    for (i = 0; i < rowCount; i++) {
        temp = '';
        if (mrvs_update_set.getRow(i).us_name != undefined && mrvs_update_set.getRow(i).us_name != null && mrvs_update_set.getRow(i).us_name != '') {
            temp = obj_client.validateUpdateSetName(current.variables.dev_environment, current.variables.release, mrvs_update_set.getRow(i).us_name);
            if (temp != '') {
                invalid_components += '\n' + temp;
            }
        }
        if (mrvs_update_set.getRow(i).script_mvrs != undefined && mrvs_update_set.getRow(i).script_mvrs != null && mrvs_update_set.getRow(i).script_mvrs != '') {
            temp = mrvs_update_set.getRow(i).script_mvrs;
            temp = temp.toString().trim();

            if (temp.indexOf('function') == -1) {
                invalid_components += '\n Below script was not encapsulated in a function:\n\n' + temp;
            }
        }
    }
    if (invalid_components == '') {
        return 'yes';
    }
    workflow.scratchpad.invalid_components = invalid_components;
    return 'no';
}

